import React, {useState} from "react";

function FormInput(props){
    const [currentSaving, setCurrentSaving] = useState('1000')
    const [yearlyContribution, setyearlyContribution] = useState('')
    const [expectedReturn, setExpectedReturn] = useState('')
    const [duration, setDuration] = useState('')

    const onChangeCurrentSavingHandler = (event) => {
        setCurrentSaving(event.target.value)
    }
   const onChangeYearlySavingHandler = (event) => {
       setyearlyContribution(event.target.value)
    }
   const onChangeExpectedInterestHandler = (event) => {
       setExpectedReturn(event.target.value)
    }
   const onChangeinvestmentDurationHandler = (event) => {
       setDuration(event.target.value)
    }

    const inputData = [{
        currentSaving,
        yearlyContribution,
        expectedReturn,
        duration
    }]

    function formSubmitHandler(event){
        event.preventDefault()
        props.onSaveInputData(inputData)
    }
    function resetHandler(event){
        setCurrentSaving('1000')
        setyearlyContribution('')
        setExpectedReturn('')
        setDuration('')
    }


    return(
        <div>
            <form onSubmit={formSubmitHandler} className="form">
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input onChange={onChangeCurrentSavingHandler} value={currentSaving} type="number" id="current-savings" />
                    </p>
                    <p>
                        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                        <input onChange={onChangeYearlySavingHandler} type="number" id="yearly-contribution" />
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input onChange={onChangeExpectedInterestHandler} type="number" id="expected-return" />
                    </p>
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input onChange={onChangeinvestmentDurationHandler} type="number" id="duration" />
                    </p>
                </div>
                <p className="actions">
                    <button type="reset" onClick={resetHandler}  className="buttonAlt">
                        Reset
                    </button>
                    <button type="submit" className="button">
                        Calculate
                    </button>
                </p>
            </form>
        </div>
    )
}

export default FormInput;
