import React, {useState} from "react";

const initialInput = {
    'current-savings' :1000,
    'yearly-contribution': 1200,
    'expected-return': 7,
    'duration': 3
}

function FormInput(props){
    const [currentSaving, setCurrentSaving] = useState('1000')
    const [yearlyContribution, setyearlyContribution] = useState('50')
    const [expectedReturn, setExpectedReturn] = useState('3')
    const [duration, setDuration] = useState('5')
    const [input, setInput] = useState(initialInput)

    const inputData = [{
        currentSaving,
        yearlyContribution,
        expectedReturn,
        duration
    }]

    function formSubmitHandler(event){
        event.preventDefault()
        props.onSaveInputData(inputData)
    }
    function resetHandler(event){
       setInput(initialInput)
    }
    function inputChangeHandler(input, value){
        setInput((prevInput)=>{
            return{
                ...prevInput,
                [input]:value,
            }
        })
    }

    return(
        <div>
            <form onSubmit={formSubmitHandler} className="form">
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input onChange={(event)=>
                            inputChangeHandler("current-savings", event.target.value)}
                               value={currentSaving} type="number" id="current-savings" />
                    </p>
                    <p>
                        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                        <input onChange={(event)=>
                            inputChangeHandler("yearly-contribution", event.target.value)}
                               value={yearlyContribution} type="number" id="yearly-contribution" />
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input onChange={(event)=>
                            inputChangeHandler("expected-return", event.target.value)}
                               value={expectedReturn} type="number" id="expected-return" />
                    </p>
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input onChange={(event)=>
                            inputChangeHandler("duration", event.target.value)}
                               value={duration} type="number" id="duration" />
                    </p>
                </div>
                <p className="actions">
                    <button type="reset" onClick={resetHandler}  className="buttonAlt">
                        Reset
                    </button>
                    <button type="submit" className="button">
                        Calculate
                    </button>
                </p>
            </form>
        </div>
    )
}

export default FormInput;
