import React, {useState} from "react";
import Header from "./Components/Header";
import FormInput from "./Components/FormInput";
import Investment from "./Components/Investment";

function App() {


  const [userInput, setInputData]=useState(null)

  const saveInputData = (userInput) =>{
    setInputData(userInput)
  }
    console.log(+userInput[0].currentSaving)


    const yearlyData = [];
 if (userInput) {
   let currentSavings = +userInput[0];
   const yearlyContribution = +userInput[1];
   const expectedReturn = +userInput[2] / 100;
   const duration = +userInput[1];

   // The below code calculates yearly results (total savings, interest etc)
   for (let i = 0; i < duration; i++) {
     const yearlyInterest = currentSavings * expectedReturn;
     currentSavings += yearlyInterest + yearlyContribution;
     yearlyData.push({
       // feel free to change the shape of the data pushed to the array!
       year: i + 1,
       yearlyInterest: yearlyInterest,
       savingsEndOfYear: currentSavings,
       yearlyContribution: yearlyContribution,
     });
   }



 }

  return (
    <div>
        <Header/>
        <FormInput onSaveInputData={saveInputData}/>

      {/* Todo: Show below table conditionally (only once result data is available) */}
      {!userInput && <p>No investment calculated yet</p>}
      {userInput && <Investment data={yearlyData} initialInvestment={userInput[1]}/>}
    </div>
  );
}

export default App;
