import React, {useState} from "react";

function FormInput(props){
    const [currentSaving, setCurrentSaving] = useState('')
    const [yearlySaving, setyearlySaving] = useState('')
    const [expectedInterest, setexpectedInterest] = useState('')
    const [investmentDuration, setinvestmentDuration] = useState('')

    const onChangeCurrentSavingHandler = (event) => {
        setCurrentSaving(event.target.value)
    }
   const onChangeYearlySavingHandler = (event) => {
       setyearlySaving(event.target.value)
    }
   const onChangeExpectedInterestHandler = (event) => {
       setexpectedInterest(event.target.value)
    }
   const onChangeinvestmentDurationHandler = (event) => {
       setinvestmentDuration(event.target.value)
    }

    const inputData = [{
        currentSaving,
        yearlySaving,
        expectedInterest,
        investmentDuration
    }]

    function formSubmitHandler(event){
        event.preventDefault()
        props.onSaveInputData(inputData)
    }
    return(
        <div>
            <form onSubmit={formSubmitHandler} className="form">
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input onChange={onChangeCurrentSavingHandler} type="number" id="current-savings" />
                    </p>
                    <p>
                        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                        <input onChange={onChangeYearlySavingHandler} type="number" id="yearly-contribution" />
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input onChange={onChangeExpectedInterestHandler} type="number" id="expected-return" />
                    </p>
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input onChange={onChangeinvestmentDurationHandler} type="number" id="duration" />
                    </p>
                </div>
                <p className="actions">
                    <button type="reset" className="buttonAlt">
                        Reset
                    </button>
                    <button type="submit" className="button">
                        Calculate
                    </button>
                </p>
            </form>
        </div>
    )
}

export default FormInput;
